package com.ftp.authservice.domain.ports.out;

import com.ftp.authservice.domain.model.User;

/**
 * This interface defines the port for saving a user to the persistence layer.
 * The implementation of this interface will handle the actual saving of a user to the database.
 */
public interface SaveUserPort {

    /**
     * Saves a new user to the persistence layer.
     *
     * @param user The user object to be saved.
     * @return The saved user object, which may include autogenerated fields such as ID.
     * @throws IllegalArgumentException if the user is invalid or contains missing information.
     */
    User save(User user) throws IllegalArgumentException;

    // Check if a username already exists in the system
    boolean existsByUsername(String username);
}
